 149	 /**
 150	 * 更新用户
 151	 */
 152	 @Override
 153	 public Result<UpdateUserVO> updateUser(UpdateUserDTO updateUserDTO) {
 154	 try {
 155	 // 1. 检查用户是否存在
 156	 User existingUser = this.getById(updateUserDTO.getId());
 157	 if (existingUser == null) {
 158	 return Result.error("用户不存在");
 159	 }
 160	
 161	 // 2. 检查用户名是否被其他用户占用
 162	 if (StringUtils.hasText(updateUserDTO.getUserName()) &&
 163	 !updateUserDTO.getUserName().equals(existingUser.getUserName())) {
 164	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 165	 queryWrapper.eq(User::getUserName, updateUserDTO.getUserName())
 166	 .ne(User::getId, updateUserDTO.getId());
 167	 User userWithSameName = this.getOne(queryWrapper);
 168	
 169	 if (userWithSameName != null) {
 170	 return Result.error("用户名已被其他用户使用");
 171	 }
 172	 }
 173	
 174	 // 3. 检查邮箱是否被其他用户占用
 175	 if (StringUtils.hasText(updateUserDTO.getEmail()) &&
 176	 !updateUserDTO.getEmail().equals(existingUser.getEmail())) {
 177	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 178	 queryWrapper.eq(User::getEmail, updateUserDTO.getEmail())
 179	 .ne(User::getId, updateUserDTO.getId());
 180	 User userWithSameEmail = this.getOne(queryWrapper);
 181	
 182	 if (userWithSameEmail != null) {
 183	 return Result.error("邮箱已被其他用户使用");
 184	 }
 185	 }
 186	
 187	 // 4. 更新用户信息
 188	 User userToUpdate = User.builder()
 189	 .id(updateUserDTO.getId())
 190	 .userName(StringUtils.hasText(updateUserDTO.getUserName()) ?
 191	 updateUserDTO.getUserName() : existingUser.getUserName())
 192	 .email(StringUtils.hasText(updateUserDTO.getEmail()) ?
 193	 updateUserDTO.getEmail() : existingUser.getEmail())
 194	 .build();
 195	
 196	 boolean updated = this.updateById(userToUpdate);
 197	 if (!updated) {
 198	 return Result.error("更新用户失败");
 199	 }
 200	
 201	 // 5. 构建返回结果
 202	 UpdateUserVO updateUserVO = UpdateUserVO.builder()
 203	 .id(userToUpdate.getId())
 204	 .userName(userToUpdate.getUserName())
 205	 .email(userToUpdate.getEmail())
 206	 .build();
 207	
 208	 return Result.success(updateUserVO);
 209	 } catch (Exception e) {
 210	 return Result.error("更新用户失败：" + e.getMessage());
 211	 }
 212	 }
