 30	
 31	
 32	 @Override
 33	 public Result<GetUserByIdVO> getUserById(GetUserByIdDTO getUserByIdDTO) {
 34	 //1.整理前端传来的查询数据
 35	 Long id = getUserByIdDTO.getId();
 36	
 37	 //2.数据库的查询
 38	 User user = userMapper.selectById(id);
 39	
 40	 //3.整理查询的数据变成vo返回
 41	 GetUserByIdVO getUserByIdVO = GetUserByIdVO.builder()
 42	 .id(user.getId())
 43	 .userName(user.getUserName())
 44	 .email(user.getEmail())
 45	 .build();
 46	
 47	 return Result.success(getUserByIdVO);
 48	 }
