 66	 @Override
 67	 public PageResult<UserListItemVO> pageUsers(UserPageQueryDTO queryDTO) {
 68	 try {
 69	 // 1. 构建查询条件
 70	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 71	 
 72	 // 2. 添加条件过滤
 73	 if (StringUtils.hasText(queryDTO.getUserName())) {
 74	 queryWrapper.like(User::getUserName, queryDTO.getUserName());
 75	 }
 76	 
 77	 if (StringUtils.hasText(queryDTO.getEmail())) {
 78	 queryWrapper.like(User::getEmail, queryDTO.getEmail());
 79	 }
...
 100	
 101	 /**
 102	 * 创建用户
 103	 */
 104	 @Override
 105	 public Result<CreateUserVO> createUser(CreateUserDTO createUserDTO) {
 106	 try {
 107	 // 1. 检查用户名是否已存在
 108	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 109	 queryWrapper.eq(User::getUserName, createUserDTO.getUserName());
 110	 User existingUser = this.getOne(queryWrapper);
 111	
 112	 if (existingUser != null) {
 113	 return Result.error("用户名已存在");
 114	 }
 115	
 116	 // 2. 检查邮箱是否已存在
 117	 queryWrapper = new LambdaQueryWrapper<>();
 118	 queryWrapper.eq(User::getEmail, createUserDTO.getEmail());
 119	 existingUser = this.getOne(queryWrapper);
 120	
 121	 if (existingUser != null) {
 122	 return Result.error("邮箱已存在");
 123	 }
 124	
 125	 // 3. 创建新用户
 126	 User newUser = User.builder()
 127	 .userName(createUserDTO.getUserName())
 128	 .email(createUserDTO.getEmail())
 129	 .build();
 130	
 131	 boolean saved = this.save(newUser);
 132	 if (!saved) {
 133	 return Result.error("创建用户失败");
 134	 }
 135	
 136	 // 4. 构建返回结果
 137	 CreateUserVO createUserVO = CreateUserVO.builder()
 138	 .id(newUser.getId())
 139	 .userName(newUser.getUserName())
 140	 .email(newUser.getEmail())
 141	 .build();
 142	
 143	 return Result.success(createUserVO);
 144	 } catch (Exception e) {
 145	 return Result.error("创建用户失败：" + e.getMessage());
 146	 }
 147	 }
 148	
 149	 /**
 150	 * 更新用户
 151	 */
 152	 @Override
 153	 public Result<UpdateUserVO> updateUser(UpdateUserDTO updateUserDTO) {
 154	 try {
 155	 // 1. 检查用户是否存在
 156	 User existingUser = this.getById(updateUserDTO.getId());
 157	 if (existingUser == null) {
 158	 return Result.error("用户不存在");
 159	 }
 160	
 161	 // 2. 检查用户名是否被其他用户占用
 162	 if (StringUtils.hasText(updateUserDTO.getUserName()) &&
 163	 !updateUserDTO.getUserName().equals(existingUser.getUserName())) {
 164	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 165	 queryWrapper.eq(User::getUserName, updateUserDTO.getUserName())
 166	 .ne(User::getId, updateUserDTO.getId());
 167	 User userWithSameName = this.getOne(queryWrapper);
 168	
 169	 if (userWithSameName != null) {
 170	 return Result.error("用户名已被其他用户使用");
 171	 }
 172	 }
 173	
 174	 // 3. 检查邮箱是否被其他用户占用
 175	 if (StringUtils.hasText(updateUserDTO.getEmail()) &&
 176	 !updateUserDTO.getEmail().equals(existingUser.getEmail())) {
 177	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 178	 queryWrapper.eq(User::getEmail, updateUserDTO.getEmail())
 179	 .ne(User::getId, updateUserDTO.getId());
 180	 User userWithSameEmail = this.getOne(queryWrapper);
 181	
 182	 if (userWithSameEmail != null) {
 183	 return Result.error("邮箱已被其他用户使用");
 184	 }
 185	 }
 186	
 187	 // 4. 更新用户信息
 188	 User userToUpdate = User.builder()
 189	 .id(updateUserDTO.getId())
 190	 .userName(StringUtils.hasText(updateUserDTO.getUserName()) ?
 191	 updateUserDTO.getUserName() : existingUser.getUserName())
 192	 .email(StringUtils.hasText(updateUserDTO.getEmail()) ?
 193	 updateUserDTO.getEmail() : existingUser.getEmail())
 194	 .build();
 195	
 196	 boolean updated = this.updateById(userToUpdate);
 197	 if (!updated) {
 198	 return Result.error("更新用户失败");
 199	 }
 200	
 201	 // 5. 构建返回结果
 202	 UpdateUserVO updateUserVO = UpdateUserVO.builder()
 203	 .id(userToUpdate.getId())
 204	 .userName(userToUpdate.getUserName())
 205	 .email(userToUpdate.getEmail())
 206	 .build();
 207	
 208	 return Result.success(updateUserVO);
 209	 } catch (Exception e) {
 210	 return Result.error("更新用户失败：" + e.getMessage());
 211	 }
 212	 }
 213	
 214	 /**
 215	 * 删除用户
 216	 */
 217	 @Override
 218	 public Result<Void> deleteUser(DeleteUserDTO deleteUserDTO) {
 219	 try {
 220	 // 1. 检查用户是否存在
 221	 User existingUser = this.getById(deleteUserDTO.getId());
 222	 if (existingUser == null) {
 223	 return Result.error("用户不存在");
 224	 }
 225	
 226	 // 2. 执行删除操作
 227	 boolean deleted = this.removeById(deleteUserDTO.getId());
 228	 if (!deleted) {
 229	 return Result.error("删除用户失败");
 230	 }
 231	
 232	 return Result.success();
 233	 } catch (Exception e) {
 234	 return Result.error("删除用户失败：" + e.getMessage());
 235	 }
 236	 }
 237	
 238	 /**
 239	 * 批量删除用户
 240	 */
 241	 @Override
 242	 public Result<Void> batchDeleteUsers(BatchDeleteUserDTO batchDeleteUserDTO) {
 243	 try {
 244	 List<Long> ids = batchDeleteUserDTO.getIds();
 245	
 246	 // 1. 检查是否有不存在的用户ID
 247	 List<User> existingUsers = this.listByIds(ids);
 248	 if (existingUsers.size() != ids.size()) {
 249	 return Result.error("部分用户不存在，无法执行批量删除");
 250	 }
 251	
 252	 // 2. 执行批量删除
 253	 boolean deleted = this.removeByIds(ids);
 254	 if (!deleted) {
 255	 return Result.error("批量删除用户失败");
 256	 }
 257	
 258	 return Result.success();
 259	 } catch (Exception e) {
 260	 return Result.error("批量删除用户失败：" + e.getMessage());
 261	 }
 262	 }
