 17	/**
 18	 * 全局异常处理器
 19	 */
 20	@Slf4j
 21	@RestControllerAdvice
 22	public class GlobalExceptionHandler {
 23	
 24	 /**
 25	 * 处理参数校验异常 (@RequestBody)
 26	 */
 27	 @ExceptionHandler(MethodArgumentNotValidException.class)
 28	 @ResponseStatus(HttpStatus.BAD_REQUEST)
 29	 public Result<Void> handleMethodArgumentNotValidException(MethodArgumentNotValidException e) {
 30	 String message = e.getBindingResult().getFieldErrors().stream()
 31	 .map(FieldError::getDefaultMessage)
 32	 .collect(Collectors.joining(", "));
 33	 log.warn("参数校验失败: {}", message);
 34	 return Result.error(400, message);
 35	 }
 36	
 37	 /**
 38	 * 处理参数校验异常 (@RequestParam, @PathVariable)
 39	 */
 40	 @ExceptionHandler(ConstraintViolationException.class)
 41	 @ResponseStatus(HttpStatus.BAD_REQUEST)
 42	 public Result<Void> handleConstraintViolationException(ConstraintViolationException e) {
 43	 String message = e.getConstraintViolations().stream()
 44	 .map(ConstraintViolation::getMessage)
 45	 .collect(Collectors.joining(", "));
 46	 log.warn("参数校验失败: {}", message);
 47	 return Result.error(400, message);
 48	 }
