Path: 测试/src/main/java/com/example/demo/service/impl/UserServiceImpl.java
...
 100	
 101	 /**
 102	 * 创建用户
 103	 */
 104	 @Override
 105	 public Result<CreateUserVO> createUser(CreateUserDTO createUserDTO) {
 106	 try {
 107	 // 1. 检查用户名是否已存在
 108	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 109	 queryWrapper.eq(User::getUserName, createUserDTO.getUserName());
 110	 User existingUser = this.getOne(queryWrapper);
 111	
 112	 if (existingUser != null) {
 113	 return Result.error("用户名已存在");
 114	 }
 115	
 116	 // 2. 检查邮箱是否已存在
 117	 queryWrapper = new LambdaQueryWrapper<>();
 118	 queryWrapper.eq(User::getEmail, createUserDTO.getEmail());
 119	 existingUser = this.getOne(queryWrapper);
 120	
 121	 if (existingUser != null) {
 122	 return Result.error("邮箱已存在");
 123	 }
 124	
 125	 // 3. 创建新用户
 126	 User newUser = User.builder()
 127	 .userName(createUserDTO.getUserName())
 128	 .email(createUserDTO.getEmail())
 129	 .build();
...
 148	
 149	 /**
 150	 * 更新用户
 151	 */
 152	 @Override
 153	 public Result<UpdateUserVO> updateUser(UpdateUserDTO updateUserDTO) {
 154	 try {
 155	 // 1. 检查用户是否存在
 156	 User existingUser = this.getById(updateUserDTO.getId());
 157	 if (existingUser == null) {
 158	 return Result.error("用户不存在");
 159	 }
 160	
 161	 // 2. 检查用户名是否被其他用户占用
 162	 if (StringUtils.hasText(updateUserDTO.getUserName()) &&
 163	 !updateUserDTO.getUserName().equals(existingUser.getUserName())) {
 164	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 165	 queryWrapper.eq(User::getUserName, updateUserDTO.getUserName())
 166	 .ne(User::getId, updateUserDTO.getId());
 167	 User userWithSameName = this.getOne(queryWrapper);
 168	
 169	 if (userWithSameName != null) {
 170	 return Result.error("用户名已被其他用户使用");
 171	 }
 172	 }
...
