 17	import org.springframework.stereotype.Service;
 18	import org.springframework.util.StringUtils;
 19	
 20	import java.util.ArrayList;
 21	import java.util.List;
 22	import java.util.stream.Collectors;
 23	
 24	@Service
 25	public class UserServiceImpl extends ServiceImpl<UserMapper, User> implements UserService {
 26	 // All basic CRUD operations are provided by ServiceImpl
 27	 
 28	 @Autowired
 29	 private UserMapper userMapper;
 30	
 31	
 32	 @Override
 33	 public Result<GetUserByIdVO> getUserById(GetUserByIdDTO getUserByIdDTO) {
 34	 //1.整理前端传来的查询数据
 35	 Long id = getUserByIdDTO.getId();
 36	
 37	 //2.数据库的查询
 38	 User user = userMapper.selectById(id);
 39	
 40	 //3.整理查询的数据变成vo返回
 41	 GetUserByIdVO getUserByIdVO = GetUserByIdVO.builder()
 42	 .id(user.getId())
 43	 .userName(user.getUserName())
 44	 .email(user.getEmail())
 45	 .build();
 46	
 47	 return Result.success(getUserByIdVO);
 48	 }
 49	
 50	 //通过用户id获得用户名称
 51	 @Override
 52	 public Result<GetUserNameByIdVO> getUserNameById(GetUserByIdDTO getUserByIdDTO) {
 53	 Long id = getUserByIdDTO.getId();
 54	
 55	 User user = userMapper.selectNameById(id);
 56	
 57	 String userName = user.getUserName();
 58	 GetUserNameByIdVO getUserNameByIdVO = new GetUserNameByIdVO();
 59	 getUserNameByIdVO.setUserName(userName);
 60	
 61	 return Result.success(getUserNameByIdVO);
 62	 }
 63	 
 64	 /**
 65	 * 分页查询用户列表
 66	 * @param queryDTO 查询参数
 67	 * @return 分页结果
 68	 */
 69	 @Override
 70	 public PageResult<UserListItemVO> pageUsers(UserPageQueryDTO queryDTO) {
 71	 try {
 72	 // 1. 构建查询条件
 73	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 74	 
 75	 // 2. 添加条件过滤
 76	 if (StringUtils.hasText(queryDTO.getUserName())) {
 77	 queryWrapper.like(User::getUserName, queryDTO.getUserName());
 78	 }
 79	 
 80	 if (StringUtils.hasText(queryDTO.getEmail())) {
 81	 queryWrapper.like(User::getEmail, queryDTO.getEmail());
 82	 }
 83	 
 84	 // 3. 创建分页对象并执行查询
 85	 Page<User> page = new Page<>(queryDTO.getCurrent(), queryDTO.getSize());
 86	 Page<User> userPage = this.page(page, queryWrapper);
 87	
 88	 // 4. 使用Stream API将User实体转换为VO对象
