 5	import lombok.Data;
 6	
 7	import java.io.Serializable;
 8	
 9	/**
 10	 * @author: 何鸿涛
 11	 * @date: 2023-08-11 19:50
 12	 * @description: 响应参数工具类
 13	 **/
 14	@Data
 15	public class Response<T> implements Serializable {
 16	
 17	 // 是否成功，默认为 true
 18	 private boolean success = true;
 19	 // 响应消息
 20	 private String message;
 21	 // 异常码
 22	 private String errorCode;
 23	 // 响应数据
 24	 private T data;
 25	
 26	 // =================================== 成功响应 ===================================
 27	 public static <T> Response<T> success() {
 28	 Response<T> response = new Response<>();
 29	 return response;
 30	 }
 31	
 32	 public static <T> Response<T> success(T data) {
 33	 Response<T> response = new Response<>();
 34	 response.setData(data);
 35	 return response;
 36	 }
 37	
 38	 // =================================== 失败响应 ===================================
 39	 public static <T> Response<T> fail() {
 40	 Response<T> response = new Response<>();
 41	 response.setSuccess(false);
 42	 return response;
 43	 }
 44	
 45	 public static <T> Response<T> fail(String errorMessage) {
 46	 Response<T> response = new Response<>();
 47	 response.setSuccess(false);
 48	 response.setMessage(errorMessage);
 49	 return response;
 50	 }
 51	
 52	 public static <T> Response<T> fail(String errorCode, String errorMessage) {
 53	 Response<T> response = new Response<>();
 54	 response.setSuccess(false);
 55	 response.setErrorCode(errorCode);
 56	 response.setMessage(errorMessage);
 57	 return response;
 58	 }
 59	
 60	 public static <T> Response<T> fail(BizException bizException) {
 61	 Response<T> response = new Response<>();
 62	 response.setSuccess(false);
 63	 response.setErrorCode(bizException.getErrorCode());
 64	 response.setMessage(bizException.getErrorMessage());
 65	 return response;
 66	 }
 67	
 68	 public static <T> Response<T> fail(BaseExceptionInterface baseExceptionInterface) {
 69	 Response<T> response = new Response<>();
 70	 response.setSuccess(false);
 71	 response.setErrorCode(baseExceptionInterface.getErrorCode());
 72	 response.setMessage(baseExceptionInterface.getErrorMessage());
 73	 return response;
 74	 }
