 9	import org.springframework.web.bind.MethodArgumentNotValidException;
 10	import org.springframework.web.bind.annotation.ControllerAdvice;
 11	import org.springframework.web.bind.annotation.ExceptionHandler;
 12	import org.springframework.web.bind.annotation.ResponseBody;
 13	
 14	import java.util.Optional;
 15	
 16	@ControllerAdvice
 17	@Slf4j
 18	public class GlobalExceptionHandler {
 19	
 20	 /**
 21	 * 捕获自定义业务异常
 22	 * @return
 23	 */
 24	 @ExceptionHandler({ BizException.class })
 25	 @ResponseBody
 26	 public Response<Object> handleBizException(HttpServletRequest request, BizException e) {
 27	 log.warn("{} request fail, errorCode: {}, errorMessage: {}", request.getRequestURI(), e.getErrorCode(), e.getErrorMessage());
 28	 return Response.fail(e);
 29	 }
 30	
 31	 /**
 32	 * 捕获参数校验异常
 33	 * @return
 34	 */
 35	 @ExceptionHandler({ MethodArgumentNotValidException.class })
 36	 @ResponseBody
 37	 public Response<Object> handleMethodArgumentNotValidException(HttpServletRequest request, MethodArgumentNotValidException e) {
 38	 // 参数错误异常码
 39	 String errorCode = ResponseCodeEnum.PARAM_NOT_VALID.getErrorCode();
 40	
 41	 // 获取 BindingResult
 42	 BindingResult bindingResult = e.getBindingResult();
 43	
 44	 StringBuilder sb = new StringBuilder();
 45	
 46	 // 获取校验不通过的字段，并组合错误信息，格式为： email 邮箱格式不正确, 当前值: '123124qq.com';
 47	 Optional.ofNullable(bindingResult.getFieldErrors()).ifPresent(errors -> {
 48	 errors.forEach(error ->
 49	 sb.append(error.getField())
 50	 .append(" ")
 51	 .append(error.getDefaultMessage())
 52	 .append(", 当前值: '")
 53	 .append(error.getRejectedValue())
 54	 .append("'; ")
 55	
 56	 );
 57	 });
 58	
 59	 // 错误信息
 60	 String errorMessage = sb.toString();
 61	
 62	 log.warn("{} request error, errorCode: {}, errorMessage: {}", request.getRequestURI(), errorCode, errorMessage);
 63	
 64	 return Response.fail(errorCode, errorMessage);
 65	 }
 66	
 67	 /**
 68	 * 捕获 guava 参数校验异常
 69	 * @return
 70	 */
 71	 @ExceptionHandler({ IllegalArgumentException.class })
 72	 @ResponseBody
 73	 public Response<Object> handleIllegalArgumentException(HttpServletRequest request, IllegalArgumentException e) {
 74	 // 参数错误异常码
 75	 String errorCode = ResponseCodeEnum.PARAM_NOT_VALID.getErrorCode();
 76	
 77	 // 错误信息
 78	 String errorMessage = e.getMessage();
 79	
 80	 log.warn("{} request error, errorCode: {}, errorMessage: {}", request.getRequestURI(), errorCode, errorMessage);
 81	
 82	 return Response.fail(errorCode, errorMessage);
 83	 }
 84	
 85	 /**
 86	 * 其他类型异常
 87	 * @param request
 88	 * @param e
 89	 * @return
 90	 */
 91	 @ExceptionHandler({ Exception.class })
 92	 @ResponseBody
 93	 public Response<Object> handleOtherException(HttpServletRequest request, Exception e) {
 94	 log.error("{} request error, ", request.getRequestURI(), e);
 95	 return Response.fail(ResponseCodeEnum.SYSTEM_ERROR);
 96	 }
 97	}
