 44	 GetUserByIdVO getUserByIdVO = GetUserByIdVO.builder()
 45	 .id(user.getId())
 46	 .userName(user.getUserName())
 47	 .email(user.getEmail())
 48	 .build();
 49	
 50	 return Result.success(getUserByIdVO);
 51	 }
