 59	 if (user == null) {
 60	 throw new UserNotFoundException(id);
 61	 }
 62	
 63	 String userName = user.getUserName();
 64	 GetUserNameByIdVO getUserNameByIdVO = new GetUserNameByIdVO();
 65	 getUserNameByIdVO.setUserName(userName);
 66	
 67	 return Result.success(getUserNameByIdVO);
