 72	 // 1. 构建查询条件
 73	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 74	 
 75	 // 2. 添加条件过滤
 76	 if (StringUtils.hasText(queryDTO.getUserName())) {
 77	 queryWrapper.like(User::getUserName, queryDTO.getUserName());
 78	 }
 79	 
 80	 if (StringUtils.hasText(queryDTO.getEmail())) {
 81	 queryWrapper.like(User::getEmail, queryDTO.getEmail());
 82	 }
 83	 
 84	 // 3. 创建分页对象并执行查询
 85	 Page<User> page = new Page<>(queryDTO.getCurrent(), queryDTO.getSize());
 86	 Page<User> userPage = this.page(page, queryWrapper);
 87	
 88	 // 4. 使用Stream API将User实体转换为VO对象
 89	 List<UserListItemVO> voList = userPage.getRecords().stream()
 90	 .map(user -> UserListItemVO.builder()
 91	 .id(user.getId())
 92	 .userName(user.getUserName())
 93	 .email(user.getEmail())
 94	 .build())
 95	 .collect(Collectors.toList());
 96	 
 97	 // 5. 使用PageResult.success方法创建分页结果
 98	 return PageResult.success(userPage, voList);
 99	 } catch (Exception e) {
 100	 return PageResult.error("查询用户列表失败：" + e.getMessage());
 101	 }
