 41	
 42	 //3.整理查询的数据变成vo返回
 43	 GetUserByIdVO getUserByIdVO = GetUserByIdVO.builder()
 44	 .id(user.getId())
 45	 .userName(user.getUserName())
 46	 .email(user.getEmail())
 47	 .build();
 48	
 49	 return Result.success(getUserByIdVO);
 50	 }
