 1	package com.example.demo.common;
 2	
 3	import lombok.Data;
 4	
 5	//自定义返参
 6	@Data
 7	public class Result<T> {
 8	 private Integer code;
 9	 private String message;
 10	 private T data;
 11	
 12	 public static <T> Result<T> success() {
 13	 Result<T> result = new Result<>();
 14	 result.setCode(200);
 15	 result.setMessage("操作成功");
 16	 return result;
 17	 }
 18	
 19	 //静态工厂
 20	 public static <T> Result<T> success(T data) {
 21	 Result<T> result = new Result<>();
 22	 result.setCode(200);
 23	 result.setMessage("操作成功");
 24	 result.setData(data);
 25	 return result;
 26	 }
 27	
 28	 public static <T> Result<T> error(String message) {
 29	 Result<T> result = new Result<>();
 30	 result.setCode(500);
 31	 result.setMessage(message);
 32	 return result;
 33	 }
 34	
 35	 public static <T> Result<T> error(Integer code, String message) {
 36	 Result<T> result = new Result<>();
 37	 result.setCode(code);
 38	 result.setMessage(message);
 39	 return result;
 40	 }
 41	
 42	 /**
 43	 * 判断是否成功
 44	 */
 45	 public boolean isSuccess() {
 46	 return this.code != null && this.code == 200;
 47	 }
 48	}...
