 11	/**
 12	 * 分页结果基类
 13	 */
 14	@Data
 15	public class PageResult<T> {
 16	 /**
 17	 * 状态码
 18	 */
 19	 private Integer code;
 20	 
 21	 /**
 22	 * 消息
 23	 */
 24	 private String message;
 25	 
 26	 /**
 27	 * 总记录数
 28	 */
 29	 private Long total;
 30	 
 31	 /**
 32	 * 总页数
 33	 */
 34	 private Long pages;
 35	 
 36	 /**
 37	 * 当前页码
 38	 */
 39	 private Long current;
 40	 
 41	 /**
 42	 * 每页显示条数
 43	 */
 44	 private Long size;
 45	 
 46	 /**
 47	 * 数据列表
 48	 */
 49	 private List<T> records;
 50	 
 51	 /**
 52	 * 从MyBatis-Plus的Page对象创建成功的分页结果
 53	 * @param page MyBatis-Plus的Page对象
 54	 * @param records 记录列表
 55	 * @return PageResult对象
 56	 */
 57	 public static <T, E> PageResult<T> success(Page<E> page, List<T> records) {
 58	 PageResult<T> result = new PageResult<>();
 59	 result.setCode(200);
 60	 result.setMessage("操作成功");
 61	 result.setTotal(page.getTotal());
 62	 result.setPages(page.getPages());
 63	 result.setCurrent(page.getCurrent());
 64	 result.setSize(page.getSize());
 65	 result.setRecords(records);
 66	 return result;
 67	 }
 68	 
 69	 /**
 70	 * 从MyBatis-Plus的Page对象创建成功的分页结果，并使用转换函数将E类型转换为T类型
 71	 * @param page MyBatis-Plus的Page对象
 72	 * @param converter 将E类型转换为T类型的函数
 73	 * @return PageResult对象
 74	 */
 75	 public static <T, E> PageResult<T> success(Page<E> page, Function<E, T> converter) {
 76	 List<T> records = page.getRecords().stream()
 77	 .map(converter)
 78	 .collect(Collectors.toList());
 79	 
 80	 return success(page, records);
 81	 }
 82	 
 83	 /**
 84	 * 创建成功的分页结果
 85	 */
 86	 public static <T> PageResult<T> success(List<T> records, Long total, Long pages, Long current, Long size) {
 87	 PageResult<T> result = new PageResult<>();
 88	 result.setCode(200);
 89	 result.setMessage("操作成功");
 90	 result.setTotal(total);
 91	 result.setPages(pages);
 92	 result.setCurrent(current);
 93	 result.setSize(size);
 94	 result.setRecords(records);
 95	 return result;
 96	 }
 97	 
 98	 /**
 99	 * 创建空的分页结果
 100	 */
 101	 public static <T> PageResult<T> empty() {
