 24	 static async getUserById(params: GetUserByIdDTO): Promise<ApiResponse<GetUserByIdVO>> {
 25	 return request.get('/api/users/getUserById', { params })
 26	 }
 27	
 28	 // 根据ID获取用户名
 29	 static async getUserNameById(params: GetUserByIdDTO): Promise<ApiResponse<GetUserNameByIdVO>> {
 30	 return request.get('/api/users/getUserNameById', { params })
 31	 }
 32	
 33	 // 分页查询用户列表
 34	 static async getPageList(params: UserPageQueryDTO): Promise<PageResponse<UserListItemVO>> {
 35	 return request.get('/api/users/page', { params })
 36	 }
 37	
 38	 // 创建用户
 39	 static async createUser(data: CreateUserDTO): Promise<ApiResponse<string>> {
 40	 return request.post('/api/users', data)
 41	 }
 42	
 43	 // 更新用户
 44	 static async updateUser(data: UpdateUserDTO): Promise<ApiResponse<string>> {
 45	 return request.put(`/api/users/${data.id}`, {
 46	 userName: data.userName,
 47	 email: data.email
 48	 })
 49	 }
 50	
 51	 // 删除用户
 52	 static async deleteUser(id: number): Promise<ApiResponse<string>> {
 53	 return request.delete(`/api/users/${id}`)
 54	 }
 55	
 56	 // 批量删除用户
 57	 static async batchDeleteUsers(ids: number[]): Promise<ApiResponse<string>> {
 58	 return request.delete('/api/users/batch', { data: { ids } })
 59	 }
 60	}
