 71	 @Override
 72	 public PageResult<UserListItemVO> pageUsers(UserPageQueryDTO queryDTO) {
 73	 try {
 74	 // 1. 构建查询条件
 75	 LambdaQueryWrapper<User> queryWrapper = new LambdaQueryWrapper<>();
 76	 
 77	 // 2. 添加条件过滤
 78	 if (StringUtils.hasText(queryDTO.getUserName())) {
 79	 queryWrapper.like(User::getUserName, queryDTO.getUserName());
 80	 }
 81	 
 82	 if (StringUtils.hasText(queryDTO.getEmail())) {
 83	 queryWrapper.like(User::getEmail, queryDTO.getEmail());
 84	 }
...
 105	
 106	 /**
 107	 * 修改用户信息
 108	 * @param updateUserDTO 修改用户参数
 109	 * @return 修改结果
 110	 */
 111	 @Override
 112	 public Result<String> updateUser(UpdateUserDTO updateUserDTO) {
 113	 try {
 114	 // 1. 参数验证
 115	 if (updateUserDTO.getId() == null) {
 116	 return Result.error("用户ID不能为空");
 117	 }
 118	
 119	 // 2. 检查用户是否存在
 120	 User existingUser = userMapper.selectById(updateUserDTO.getId());
 121	 if (existingUser == null) {
 122	 return Result.error("用户不存在");
 123	 }
 124	
 125	 // 3. 构建更新对象
 126	 User updateUser = User.builder()
 127	 .id(updateUserDTO.getId())
 128	 .userName(updateUserDTO.getUserName())
 129	 .email(updateUserDTO.getEmail())
 130	 .build();
 131	
 132	 // 4. 执行更新
 133	 int result = userMapper.updateById(updateUser);
 134	 if (result > 0) {
 135	 return Result.success("用户信息修改成功");
 136	 } else {
 137	 return Result.error("用户信息修改失败");
 138	 }
 139	 } catch (Exception e) {
 140	 return Result.error("修改用户信息失败：" + e.getMessage());
 141	 }
 142	 }
 143	
 144	 /**
 145	 * 删除用户
 146	 * @param deleteUserDTO 删除用户参数
 147	 * @return 删除结果
 148	 */
 149	 @Override
 150	 public Result<String> deleteUser(DeleteUserDTO deleteUserDTO) {
 151	 try {
 152	 // 1. 参数验证
 153	 if (deleteUserDTO.getId() == null) {
 154	 return Result.error("用户ID不能为空");
 155	 }
 156	
 157	 // 2. 检查用户是否存在
 158	 User existingUser = userMapper.selectById(deleteUserDTO.getId());
 159	 if (existingUser == null) {
 160	 return Result.error("用户不存在");
 161	 }
 162	
 163	 // 3. 执行删除
 164	 int result = userMapper.deleteById(deleteUserDTO.getId());
 165	 if (result > 0) {
 166	 return Result.success("用户删除成功");
 167	 } else {
 168	 return Result.error("用户删除失败");
 169	 }
 170	 } catch (Exception e) {
 171	 return Result.error("删除用户失败：" + e.getMessage());
 172	 }
 173	 }
