 208	// 批量删除
 209	const handleBatchDelete = async () => {
 210	 if (selectedUsers.value.length === 0) {
 211	 ElMessage.warning('请选择要删除的用户')
 212	 return
 213	 }
 214	
 215	 try {
 216	 await ElMessageBox.confirm(
 217	 `确定要删除选中的 ${selectedUsers.value.length} 个用户吗？`,
 218	 '批量删除确认',
 219	 {
 220	 confirmButtonText: '确定',
 221	 cancelButtonText: '取消',
 222	 type: 'warning'
 223	 }
 224	 )
 225	 
 226	 const ids = selectedUsers.value.map(user => user.id)
 227	 await userStore.batchDeleteUsers(ids)
 228	 selectedUsers.value = []
 229	 } catch (error) {
 230	 if (error !== 'cancel') {
 231	 console.error('批量删除用户失败:', error)
 232	 }
 233	 }
 234	}
