 3	import com.baomidou.mybatisplus.core.handlers.MetaObjectHandler;
 4	import lombok.extern.slf4j.Slf4j;
 5	import org.apache.ibatis.reflection.MetaObject;
 6	import org.springframework.stereotype.Component;
 7	
 8	import java.time.LocalDateTime;
 9	
 10	/**
 11	 * MyBatis-Plus 自动填充处理器
 12	 * 用于自动填充创建时间和更新时间
 13	 */
 14	@Slf4j
 15	@Component
 16	public class MetaObjectHandler implements com.baomidou.mybatisplus.core.handlers.MetaObjectHandler {
 17	
 18	 /**
 19	 * 插入时自动填充
 20	 */
 21	 @Override
 22	 public void insertFill(MetaObject metaObject) {
 23	 log.info("开始插入填充...");
 24	
 25	 // 自动填充创建时间
 26	 this.strictInsertFill(metaObject, "createTime", LocalDateTime.class, LocalDateTime.now());
 27	
 28	 // 自动填充更新时间
 29	 this.strictInsertFill(metaObject, "updateTime", LocalDateTime.class, LocalDateTime.now());
 30	
 31	 // 自动填充逻辑删除字段为0（未删除）
 32	 this.strictInsertFill(metaObject, "deleted", Integer.class, 0);
 33	 }
 34	
 35	 /**
 36	 * 更新时自动填充
 37	 */
 38	 @Override
 39	 public void updateFill(MetaObject metaObject) {
 40	 log.info("开始更新填充...");
 41	 
 42	 // 自动填充更新时间
 43	 this.strictUpdateFill(metaObject, "updateTime", LocalDateTime.class, LocalDateTime.now());
 44	 }
