Path: 测试/src/main/java/com/example/demo/service/impl/UserServiceImpl.java
...
 30	
 31	@Service
 32	public class UserServiceImpl extends ServiceImpl<UserMapper, User> implements UserService {
 33	 // All basic CRUD operations are provided by ServiceImpl
 34	
 35	 @Autowired
 36	 private UserMapper userMapper;
 37	
 38	
 39	 @Override
 40	 public Result<List<UserListItemVO>> userlistitem() {
 41	 List<User> users = userMapper.selectList(null);
 42	 List<UserListItemVO> collect = users.stream().map(user ->
 43	 UserListItemVO.builder()
 44	 .userId(user.getId())
 45	 .userName(user.getUserName())
 46	 .email(user.getEmail())
 47	 .address(user.getAddress())
 48	 .age(user.getAge())
 49	 .heightCm(user.getHeightCm())
 50	 .weightKg(user.getWeightKg())
 51	 .gpa(user.getGpa())
 52	 .build())
 53	 .collect(Collectors.toList());
 54	
 55	 if(collect.isEmpty()){
 56	 throw new BizException(ResponseCodeEnum.SYSTEM_ERROR);
 57	 }
 58	
 59	 return Result.success(collect);
 60	 }
