 1	package com.example.demo.service.impl;
 2	
 3	import com.baomidou.mybatisplus.core.conditions.query.LambdaQueryWrapper;
 4	import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;
 5	import com.example.demo.common.Result;
 6	import com.example.demo.enums.ResponseCodeEnum;
 7	import com.example.demo.exception.BizException;
 8	import com.example.demo.mapper.NoteTagMapper;
 9	import com.example.demo.mapper.TagMapper;
 10	import com.example.demo.model.dataobject.NoteTag;
 11	import com.example.demo.model.dataobject.Tag;
 12	import com.example.demo.model.vo.TagVO;
 13	import com.example.demo.service.TagService;
 14	import lombok.extern.slf4j.Slf4j;
 15	import org.springframework.beans.factory.annotation.Autowired;
 16	import org.springframework.stereotype.Service;
 17	import org.springframework.transaction.annotation.Transactional;
 18	
 19	import java.util.List;
 20	import java.util.Objects;
 21	import java.util.stream.Collectors;
 22	
 23	/**
 24	 * 标签Service实现类
 25	 */
 26	@Slf4j
 27	@Service
 28	public class TagServiceImpl extends ServiceImpl<TagMapper, Tag> implements TagService {
 29	 
 30	 @Autowired
 31	 private TagMapper tagMapper;
 32	 
 33	 @Autowired
 34	 private NoteTagMapper noteTagMapper;
 35	 
 36	 @Override
 37	 public Result<List<TagVO>> getAllTags() {
 38	 List<Tag> tags = tagMapper.selectList(null);
 39	 List<TagVO> tagVOs = tags.stream()
 40	 .map(this::convertToVO)
 41	 .collect(Collectors.toList());
 42	 return Result.success(tagVOs);
 43	 }
 44	 
 45	 @Override
 46	 @Transactional(rollbackFor = Exception.class)
 47	 public Result<?> addTag(String tagName) {
 48	 // 检查标签名称是否已存在
 49	 LambdaQueryWrapper<Tag> wrapper = new LambdaQueryWrapper<>();
 50	 wrapper.eq(Tag::getTagName, tagName);
 51	 Tag existingTag = tagMapper.selectOne(wrapper);
 52	 
 53	 if (Objects.nonNull(existingTag)) {
 54	 throw new BizException(ResponseCodeEnum.TAG_NAME_EXISTS);
 55	 }
 56	 
 57	 Tag tag = Tag.builder()
 58	 .tagName(tagName)
 59	 .build();
 60	 tagMapper.insert(tag);
 61	 
 62	 return Result.success();
 63	 }
 64	 
