 8	
 9	@Configuration
 10	public class CorsConfig {
 11	
 12	 @Bean
 13	 public CorsFilter corsFilter() {
 14	 UrlBasedCorsConfigurationSource source = new UrlBasedCorsConfigurationSource();
 15	 CorsConfiguration config = new CorsConfiguration();
 16	 
 17	 // 允许的域名
 18	 config.addAllowedOrigin("http://localhost:8081");
 19	 
 20	 // 允许的请求头
 21	 config.addAllowedHeader("*");
 22	 
 23	 // 允许的HTTP方法
 24	 config.addAllowedMethod("*");
 25	 
 26	 // 允许发送Cookie
 27	 config.setAllowCredentials(true);
 28	 
 29	 // 对所有接口应用CORS配置
 30	 source.registerCorsConfiguration("/**", config);
 31	 
 32	 return new CorsFilter(source);
 33	 }
 34	} ...
