 31	@Service
 32	public class UserServiceImpl extends ServiceImpl<UserMapper, User> implements UserService {
 33	 // All basic CRUD operations are provided by ServiceImpl
 34	
 35	 @Autowired
 36	 private UserMapper userMapper;
 37	
 38	
 39	 @Override
 40	 public Result<List<UserListItemVO>> userlistitem() {
 41	 List<User> users = userMapper.selectList(null);
 42	 List<UserListItemVO> collect = users.stream().map(user ->
 43	 UserListItemVO.builder()
 44	 .userId(user.getId())
 45	 .userName(user.getUserName())
 46	 .email(user.getEmail())
 47	 .address(user.getAddress())
 48	 .age(user.getAge())
 49	 .heightCm(user.getHeightCm())
 50	 .weightKg(user.getWeightKg())
 51	 .gpa(user.getGpa())
 52	 .build())
 53	 .collect(Collectors.toList());
 54	
 55	 if(collect.isEmpty()){
 56	 throw new BizException(ResponseCodeEnum.SYSTEM_ERROR);
 57	 }
 58	
 59	 return Result.success(collect);
 60	 }
 61	
 62	 @Override
 63	 public Result<GetUserByIdVO> getUserById(GetUserByIdDTO getUserByIdDTO) {
 64	 User user = userMapper.selectById(getUserByIdDTO.getUserId());
 65	
 66	 GetUserByIdVO getUserByIdVO1 = new GetUserByIdVO();
 67	 BeanUtils.copyProperties(user,getUserByIdVO1);
 68	
 69	// GetUserByIdVO getUserByIdVO = GetUserByIdVO.builder()
 70	// .userId(user.getUserId())
 71	// .userName(user.getUserName())
 72	// .email(user.getEmail())
 73	// .build();
 74	
 75	 return Result.success(getUserByIdVO1);
 76	 }
 77	
 78	 @Override
 79	 public Result<GetUserNameByIdVO> getUserNameById(GetUserByIdDTO getUserByIdDTO) {
 80	 User user = userMapper.selectNameById(getUserByIdDTO.getUserId());
 81	 GetUserNameByIdVO getUserNameByIdVO = GetUserNameByIdVO.builder().userName(user.getUserName()).build();
 82	 return Result.success(getUserNameByIdVO);
 83	 }
...
