 169	 BeanUtils.copyProperties(dto,user);
 170	
 171	 try {
 172	 int insert = userMapper.insert(user);
 173	 return Result.success();
 174	 }catch (Exception e){
 175	 log.error("添加失败");
 176	 }
 177	
 178	
 179	 return Result.success();
 180	 }
