 26	 this.strictInsertFill(metaObject, "createTime", LocalDateTime.class, LocalDateTime.now());
 27	
 28	 // 自动填充更新时间
 29	 this.strictInsertFill(metaObject, "updateTime", LocalDateTime.class, LocalDateTime.now());
 30	
 31	 // 自动填充逻辑删除字段为0（未删除）
 32	 this.strictInsertFill(metaObject, "deleted", Integer.class, 0);
 33	 }
