 31	@Service
 32	public class UserServiceImpl extends ServiceImpl<UserMapper, User> implements UserService {
 33	 // All basic CRUD operations are provided by ServiceImpl
 34	
 35	 @Autowired
 36	 private UserMapper userMapper;
 37	
 38	
 39	 @Override
 40	 public Result<List<UserListItemVO>> userlistitem() {
 41	 List<User> users = userMapper.selectList(null);
 42	 List<UserListItemVO> collect = users.stream().map(user ->
 43	 UserListItemVO.builder()
 44	 .userId(user.getId())
 45	 .userName(user.getUserName())
 46	 .email(user.getEmail())
 47	 .address(user.getAddress())
 48	 .age(user.getAge())
 49	 .heightCm(user.getHeightCm())
 50	 .weightKg(user.getWeightKg())
 51	 .gpa(user.getGpa())
 52	 .build())
 53	 .collect(Collectors.toList());
 54	
 55	 if(collect.isEmpty()){
 56	 throw new BizException(ResponseCodeEnum.SYSTEM_ERROR);
 57	 }
 58	
 59	 return Result.success(collect);
 60	 }
...
 129	
 130	 @Override
 131	 public PageResult<UserListItemVO> getUserByPage(UserPageQueryDTO userPageQueryDTO) {
 132	 Integer current = userPageQueryDTO.getCurrent();
 133	 Integer size = userPageQueryDTO.getSize();
 134	 String userName = userPageQueryDTO.getUserName();
 135	 String email = userPageQueryDTO.getEmail();
 136	
 137	 LambdaQueryWrapper<User> wrapper = new LambdaQueryWrapper<>();
 138	 wrapper.like(StrUtil.isNotBlank(userName), User::getUserName, userName)
 139	 .like(StrUtil.isNotBlank(email), User::getEmail, email)
 140	 ;
 141	
 142	 Page<User> Page = new Page<>(current, size);
 143	
 144	 Page<User> userPage = userMapper.selectPage(Page, wrapper);
 145	 if(Objects.isNull(userPage)){
 146	 throw new BizException(ResponseCodeEnum.PAGE_NOT_FOUND);
 147	 }
 148	
 149	 List<UserListItemVO> collect = userPage.getRecords().stream().
 150	 map(user -> UserListItemVO.builder()
 151	 .userId(user.getUserId())
 152	 .userName(user.getUserName())
 153	 .email(user.getEmail())
 154	 .address(user.getAddress())
 155	 .age(user.getAge())
 156	 .heightCm(user.getHeightCm())
 157	 .weightKg(user.getWeightKg())
 158	 .gpa(user.getGpa())
 159	 .build())
 160	 .toList();
 161	
 162	 return PageResult.success(userPage,collect);
 163	 }
