 23	/**
 24	 * 标签Service实现类
 25	 */
 26	@Slf4j
 27	@Service
 28	public class TagServiceImpl extends ServiceImpl<TagMapper, Tag> implements TagService {
 29	 
 30	 @Autowired
 31	 private TagMapper tagMapper;
 32	 
 33	 @Autowired
 34	 private NoteTagMapper noteTagMapper;
 35	 
 36	 @Override
 37	 public Result<List<TagVO>> getAllTags() {
 38	 List<Tag> tags = tagMapper.selectList(null);
 39	 List<TagVO> tagVOs = tags.stream()
 40	 .map(this::convertToVO)
 41	 .collect(Collectors.toList());
 42	 return Result.success(tagVOs);
 43	 }
 44	 
 45	 @Override
 46	 @Transactional(rollbackFor = Exception.class)
 47	 public Result<?> addTag(String tagName) {
 48	 // 检查标签名称是否已存在
 49	 LambdaQueryWrapper<Tag> wrapper = new LambdaQueryWrapper<>();
 50	 wrapper.eq(Tag::getTagName, tagName);
 51	 Tag existingTag = tagMapper.selectOne(wrapper);
 52	 
 53	 if (Objects.nonNull(existingTag)) {
 54	 throw new BizException(ResponseCodeEnum.TAG_NAME_EXISTS);
 55	 }
 56	 
 57	 Tag tag = Tag.builder()
 58	 .tagName(tagName)
 59	 .build();
 60	 tagMapper.insert(tag);
 61	 
 62	 return Result.success();
 63	 }
 64	 
 65	 @Override
 66	 @Transactional(rollbackFor = Exception.class)
 67	 public Result<?> updateTag(Integer tagId, String tagName) {
 68	 Tag tag = tagMapper.selectById(tagId);
 69	 if (Objects.isNull(tag)) {
 70	 throw new BizException(ResponseCodeEnum.TAG_NOT_FOUND);
 71	 }
 72	 
 73	 // 检查标签名称是否已存在（排除当前标签）
 74	 LambdaQueryWrapper<Tag> wrapper = new LambdaQueryWrapper<>();
 75	 wrapper.eq(Tag::getTagName, tagName)
 76	 .ne(Tag::getId, tagId);
 77	 Tag existingTag = tagMapper.selectOne(wrapper);
 78	 
 79	 if (Objects.nonNull(existingTag)) {
 80	 throw new BizException(ResponseCodeEnum.TAG_NAME_EXISTS);
 81	 }
 82	 
 83	 tag.setTagName(tagName);
 84	 tagMapper.updateById(tag);
 85	 
 86	 return Result.success();
 87	 }
