 24	@Slf4j
 25	@Service
 26	public class CategoryServiceImpl extends ServiceImpl<CategoryMapper, Category> implements CategoryService {
 27	 
 28	 @Autowired
 29	 private CategoryMapper categoryMapper;
 30	 
 31	 @Override
 32	 public Result<List<CategoryVO>> getAllCategories() {
 33	 List<Category> categories = categoryMapper.selectList(null);
 34	 List<CategoryVO> categoryVOs = categories.stream()
 35	 .map(this::convertToVO)
 36	 .collect(Collectors.toList());
 37	 return Result.success(categoryVOs);
 38	 }
 39	 
 40	 @Override
 41	 @Transactional(rollbackFor = Exception.class)
 42	 public Result<?> addCategory(String categoryName) {
 43	 // 检查分类名称是否已存在
 44	 LambdaQueryWrapper<Category> wrapper = new LambdaQueryWrapper<>();
 45	 wrapper.eq(Category::getCategoryName, categoryName);
 46	 Category existingCategory = categoryMapper.selectOne(wrapper);
 47	 
 48	 if (Objects.nonNull(existingCategory)) {
 49	 throw new BizException(ResponseCodeEnum.CATEGORY_NAME_EXISTS);
 50	 }
 51	 
 52	 Category category = Category.builder()
 53	 .categoryName(categoryName)
 54	 .build();
 55	 categoryMapper.insert(category);
 56	 
 57	 return Result.success();
 58	 }
 59	 
 60	 @Override
 61	 @Transactional(rollbackFor = Exception.class)
 62	 public Result<?> updateCategory(Integer categoryId, String categoryName) {
 63	 Category category = categoryMapper.selectById(categoryId);
 64	 if (Objects.isNull(category)) {
 65	 throw new BizException(ResponseCodeEnum.CATEGORY_NOT_FOUND);
 66	 }
 67	 
 68	 // 检查分类名称是否已存在（排除当前分类）
 69	 LambdaQueryWrapper<Category> wrapper = new LambdaQueryWrapper<>();
 70	 wrapper.eq(Category::getCategoryName, categoryName)
 71	 .ne(Category::getId, categoryId);
 72	 Category existingCategory = categoryMapper.selectOne(wrapper);
 73	 
 74	 if (Objects.nonNull(existingCategory)) {
 75	 throw new BizException(ResponseCodeEnum.CATEGORY_NAME_EXISTS);
 76	 }
 77	 
 78	 category.setCategoryName(categoryName);
 79	 categoryMapper.updateById(category);
 80	 
 81	 return Result.success();
 82	 }
